import {TASK_PROJECT,ADD_TASK, VALIDATE_TASK, DELETE_TASK} from '../../types'
const TaskReducer = (state, action) => {
    switch(action.type){
        case TASK_PROJECT :
            return {
                ...state,
                tasksproject: state.tasks.filter(task => task.projectId === action.payload)
           }
        case ADD_TASK :
            return {
                ...state,
                tasks: [ action.payload, ...state.tasks],
                errortask: false
            }
        case VALIDATE_TASK:
            return {
                ...state,
                errortask: true
            }
        case DELETE_TASK:
            return{
                ...state,
                tasks: state.tasks.filter(task => task.id !== action.payload)
            }
        default: 
        return state
    }
}

export default TaskReducer
