import {TASK_PROJECT} from '../../types'
const TaskReducer = (state, action) => {
    switch(action.type){
        case TASK_PROJECT :
            return {
                ...state,
                tasksproject: state.tasksproject.filter(task => task.projectId === action.payload)
           }

        default: 
        return state
    }
}

export default TaskReducer
