import {TASK_PROJECT,ADD_TASK, VALIDATE_TASK} from '../../types'
const TaskReducer = (state, action) => {
    switch(action.type){
        case TASK_PROJECT :
            return {
                ...state,
                tasksproject: state.tasks.map(task => task.projectId === action.payload)
           }
        case ADD_TASK :
            return {
                ...state,
                tasks: [...state.tasks, action.payload],
                errortask: false
            }
        case VALIDATE_TASK:
            return {
                ...state,
                errortask: true
            }
        default: 
        return state
    }
}

export default TaskReducer
