import { Form, Button} from 'react-bootstrap';
import React, { useState } from 'react'
import { Link } from 'react-router-dom';

const NewAccount = () => {
    //Creo el state de usuarios
    const [user, saveUser] = useState({
        email: '',
        password: '',
        name: '',
        confirm: ''
    })
    //Destructuro el state para validar values
    const {email, password, name, confirm}= user;


    const onChange = e => {
        saveUser({
            //Evito que se reescriban los value con ...user
            ...user,
            [e.target.name] : e.target.value
        })
    }

    //Funcion para que el usuario inicie sesión
    const submit = e => {
        e.preventDefault()
    }
    //Valido que no haya campos vacios

    //Pasarlo al action
    return (
        <div className="container">
            <h1>Create account </h1>
            <Form>
                <Form.Group className="mb-3">
                    <Form.Label>Nombre</Form.Label>
                    <Form.Control value={name} type="text" id="name" name="name" placeholder="Your name" onChange={onChange}></Form.Control>
                </Form.Group>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label>Email address</Form.Label>
                    <Form.Control value={email} type="email" placeholder="Enter email" id="email" name="email" onChange={onChange}/>
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicPassword">
                    <Form.Label>Password</Form.Label>
                    <Form.Control value={password} type="password" placeholder="Password" name="password" id="password" onChange={onChange}/>
                </Form.Group>
                <Form.Group className="mb-3" controlId="formBasicPassword">
                    <Form.Label>Confirm password</Form.Label>
                    <Form.Control value={password} type="password" placeholder="Confirm your password" name="confirmar" id="confirm" onChange={onChange}/>
                </Form.Group>
                <Button onClick={submit} variant="primary" type="submit">
                    Send
                </Button>
            </Form>

            <Link to={'/new-account'}>Create account</Link>
        </div>
    )
}

export default NewAccount
