import React, { useContext, useState } from 'react'
import projectContext from '../../context/projects/projectContext'
import taskContext from '../../context/tasks/TaskContext'


const FormTask = () => {
     //Si proyecto esta activo
     const projectsContext = useContext(projectContext)
     const {project } = projectsContext
        //obtener task context 

        const tasksContext = useContext(taskContext)
        const {errortask, getTask, validateTask} = tasksContext
     //state form 
     const [task, saveTask] = useState({
         name: '',
     })

     //extraer el nombre del proyecto
     const { name } = task
      //Sin proyectos 
    if(!project) return null

    //Array destructuring 
    const  [projectNow] = project
     //Leer valores del form
     const handleChange = e => {
         saveTask({
             ...task,
             [e.target.name]: e.target.value
         })
     }
    const onSubmit = e => {
        e.preventDefault()
        
        //validar
        if(name.trim() === ''){
            validateTask()
            return
        }
        //pasar la validacion

        //agregar la tarea al state
        task.projectId = projectNow.id
        task.state= false
        getTask()
        //reiniciar form
    }
    return (
        <div>
            <form onSubmit={onSubmit}>
                <div>
                    <input type="text"  value={name} onChange={handleChange} placeholder="Name task" name="name"/>
                </div>
                <div>
                    <button>Add Task</button>
                </div>
            </form>
            {errortask ? <p>El nombre de la tarea es obligatorio</p> : null}
        </div>
    )
}

export default FormTask
