import {  useReducer } from "react";
import {TASK_PROJECT, ADD_TASK, VALIDATE_TASK} from '../../types'
import TaskContext from './TaskContext'
import TaskReducer from './TaskReducer'


const TaskState = props => {
    const initialState = {
        tasks: 
        [
        {name: 'elegir pa', estado:true, projectId: 1},
        {name: 'elegir colores', estado:false, projectId: 2},
        {name: 'elegir pago', estado:false, projectId: 3},
        {name: 'elegir host', estado:true , projectId:4 }
    ],
    tasksproject : null,
    errortask: false
    }
    

    //Crear disptach y state

    const [state, dispatch] = useReducer(TaskReducer, initialState)
    
    //Funciones

    //Obtener tareas
    const getTasks = projectId => {
        dispatch({
            type: TASK_PROJECT,
            payload: projectId
        })
    }
    //Agregar tarea al proyecto
    const addTask = task => {
        dispatch({
            type: ADD_TASK,
            payload: task
        })
    }

    //Valida y muestra error
    const validateTask = () =>{
        dispatch({
            type: VALIDATE_TASK,
        })
    }
    return (
        <TaskContext.Provider value={{
            tasks: state.tasks,
            tasksproject: state.tasksproject,
            errortask: state.errortask,
            validateTask,
            getTasks,
            addTask
        }}>
            {props.children}
        </TaskContext.Provider>
    )
}

export default TaskState
