import React, { useContext } from 'react'
import Task from './Task'
import projectContext from '../../context/projects/projectContext'

const ListTask = () => {
    //Obtener el state
    const projectsContext = useContext(projectContext)
    const {project } = projectsContext
    //Sin proyectos 
    if(!project) return <h2>Selecciona un proyecto</h2>

    //Array destructuring 
    const  [projectNow,deleteProject ] = project

    const tasksProject = [
        {name: 'elegir pa', estado:true},
        {name: 'elegir colores', estado:false},
        {name: 'elegir pago', estado:false},
        {name: 'elegir host', estado:true}
    ]
    return (
        <div>
            <h2>Project: {projectNow.name}</h2>
            <ul>
                {tasksProject.length === 0 
                ? (<li><p>No hay tareas</p></li>)
                : tasksProject.map(task => (
                    <Task task={task} />
                ))
            }
            </ul>
            <button>Delete Project &times;</button>
        </div>
    )
}

export default ListTask
